var request = new sn_ws.RESTMessageV2();
request.setEndpoint(gs.getProperty("x_572969_demo_exm.exm_url") + "/eem");
request.setHttpMethod('POST');
request.setRequestHeader("Accept","application/json");

var i = 1;
var department_list = '';
var current_department = current.department.sys_id;
while (i <= 5 && current_department) {
  var dep = new GlideRecord('cmn_department');
  gs.info("12.1:" + current_department);
  var qc = dep.addQuery('sys_id', current_department);
  dep.query();
  dep.next();
  department_list += dep.getValue('name') + '~~~';
  current_department = dep.getValue('parent');
  i++;
}

var department_array = department_list.split("~~~");
department_list = '';
var j = department_array.length - 1;
for (i=0; i < department_array.length - 1; i++) {
   department_list += ',"hl' + j + '":"' + department_array[i] + '"';
	j--;
}


var query = '{"Envelope":{"Body":{"querySNChangeUser":{"client_id":"' + gs.getProperty('x_572969_demo_exm.exm_id_agree') + '","sys_id":"' + current.sys_id + '","employee_number":"' + current.employee_number + '","last_name":"' + current.last_name + '","first_name":"' + current.first_name + '","email":"' + current.email +'","mobile_phone":"' + current.mobile_phone + '"' + department_list + '}}}}';

gs.info("12:" + query);

request.setRequestBody(query);
